\n#!/bin/bash\n\necho \"üîç Logan Security Dashboard Setup Verification\"\necho \"=============================================\"\n\n# Colors for output\nRED='\\033[0;31m'\nGREEN='\\033[0;32m'\nYELLOW='\\033[1;33m'\nBLUE='\\033[0;34m'\nNC='\\033[0m' # No Color\n\nprint_check() {\n    if [ $1 -eq 0 ]; then\n        echo -e \"${GREEN}‚úÖ${NC} $2\"\n    else\n        echo -e \"${RED}‚ùå${NC} $2\"\n    fi\n}\n\nprint_info() {\n    echo -e \"${BLUE}‚ÑπÔ∏è${NC} $1\"\n}\n\nprint_warning() {\n    echo -e \"${YELLOW}‚ö†Ô∏è${NC} $1\"\n}\n\n# Check if in correct directory\nif [ ! -f \"package.json\" ]; then\n    echo -e \"${RED}‚ùå${NC} Not in Logan Security Dashboard directory\"\n    echo \"Please run this script from the logan-security-dashboard directory\"\n    exit 1\nfi\n\necho \"\"\nprint_info \"Checking prerequisites...\"\n\n# Check Node.js\nnode --version >/dev/null 2>&1\nprint_check $? \"Node.js is installed\"\n\n# Check npm\nnpm --version >/dev/null 2>&1\nprint_check $? \"npm is available\"\n\n# Check Python\npython3 --version >/dev/null 2>&1\nprint_check $? \"Python 3 is installed\"\n\n# Check pip\npip3 --version >/dev/null 2>&1\nprint_check $? \"pip3 is available\"\n\n# Check OCI CLI\noci --version >/dev/null 2>&1\nprint_check $? \"OCI CLI is installed\"\n\necho \"\"\nprint_info \"Checking project setup...\"\n\n# Check if node_modules exists\n[ -d \"node_modules\" ]\nprint_check $? \"Node.js dependencies installed\"\n\n# Check if .env.local exists\n[ -f \".env.local\" ]\nprint_check $? \".env.local configuration file exists\"\n\n# Check if Python dependencies are installed\npython3 -c \"import oci\" >/dev/null 2>&1\nprint_check $? \"OCI Python SDK is installed\"\n\necho \"\"\nprint_info \"Checking OCI configuration...\"\n\n# Check OCI config file\n[ -f \"$HOME/.oci/config\" ]\nprint_check $? \"OCI config file exists\"\n\n# Check OCI authentication\nif command -v oci >/dev/null 2>&1; then\n    oci iam user list --auth-purpose service >/dev/null 2>&1\n    print_check $? \"OCI CLI authentication works\"\nelse\n    print_warning \"OCI CLI not found, skipping authentication test\"\nfi\n\necho \"\"\nprint_info \"Checking configuration values...\"\n\nif [ -f \".env.local\" ]; then\n    # Check if required variables are set\n    if grep -q \"NEXT_PUBLIC_LOGAN_REGION=\" .env.local && ! grep -q \"NEXT_PUBLIC_LOGAN_REGION=your-\" .env.local; then\n        echo -e \"${GREEN}‚úÖ${NC} LOGAN_REGION is configured\"\n    else\n        echo -e \"${RED}‚ùå${NC} LOGAN_REGION needs to be configured in .env.local\"\n    fi\n    \n    if grep -q \"NEXT_PUBLIC_LOGAN_COMPARTMENT_ID=\" .env.local && ! grep -q \"NEXT_PUBLIC_LOGAN_COMPARTMENT_ID=ocid1.compartment.oc1..your-\" .env.local; then\n        echo -e \"${GREEN}‚úÖ${NC} LOGAN_COMPARTMENT_ID is configured\"\n    else\n        echo -e \"${RED}‚ùå${NC} LOGAN_COMPARTMENT_ID needs to be configured in .env.local\"\n    fi\n    \n    if grep -q \"NEXT_PUBLIC_LOGAN_NAMESPACE=\" .env.local && ! grep -q \"NEXT_PUBLIC_LOGAN_NAMESPACE=your-\" .env.local; then\n        echo -e \"${GREEN}‚úÖ${NC} LOGAN_NAMESPACE is configured\"\n    else\n        echo -e \"${RED}‚ùå${NC} LOGAN_NAMESPACE needs to be configured in .env.local\"\n    fi\n    \n    if grep -q \"LOGAN_MCP_SERVER_PATH=\" .env.local && ! grep -q \"LOGAN_MCP_SERVER_PATH=/path/to/\" .env.local; then\n        echo -e \"${GREEN}‚úÖ${NC} MCP_SERVER_PATH is configured\"\n    else\n        echo -e \"${RED}‚ùå${NC} MCP_SERVER_PATH needs to be configured in .env.local\"\n    fi\nelse\n    echo -e \"${RED}‚ùå${NC} .env.local file not found\"\nfi\n\necho \"\"\nprint_info \"Testing build process...\"\n\n# Test build\nnpm run build >/dev/null 2>&1\nprint_check $? \"Application builds successfully\"\n\necho \"\"\nprint_info \"Summary:\"\necho \"=========\"\n\nif [ -f \".env.local\" ] && command -v oci >/dev/null 2>&1 && [ -d \"node_modules\" ]; then\n    echo -e \"${GREEN}üéâ Setup appears to be complete!${NC}\"\n    echo \"\"\n    echo \"Next steps:\"\n    echo \"1. Start the MCP server (see README.md for details)\"\n    echo \"2. Run: ./start-dashboard.sh\"\n    echo \"3. Open: http://localhost:3000\"\nelse\n    echo -e \"${YELLOW}‚ö†Ô∏è Setup needs attention${NC}\"\n    echo \"\"\n    echo \"Please address the issues above and run this script again.\"\n    echo \"See README.md for detailed setup instructions.\"\nfi\n\necho \"\"\necho \"For support, check the README.md file or create an issue.\"\n
